require 'uri'
require File.expand_path(File.join(File.dirname(__FILE__), "..", "support", "paths.rb"))

module WithinHelpers
  def with_scope(locator)
    if locator
      page.should have_css(locator, :visible => true)
      within(locator) { yield }
    else
      yield
    end
  end
end
