When /^(?:|I )fill in the following(?: within "([^\"]*)")?:$/ do |selector, fields|
  with_scope(selector) do
    fields.rows_hash.each do |name, value|
      fill_in(name, :with => value)
      # When %{I fill in "#{name}" with "#{value}"}
    end
  end
end

